var log = require('debug')('actions:index')

var c = {

  ROUTE: {
    TRANSITION: "ROUTE:TRANSITION",
    GROUP: {
      SELECT: "ROUTE:GROUP:SELECT"
    }
  },

  GROUP: {
    SELECT: "GROUP:SELECT"
  }

};

var methods = {


  routes: {
    transition: function (path, params, query) {
      this.dispatch(c.ROUTE.TRANSITION, { path: path, params: params, query: query });
    }
  },


  group: {
    select: function (groupType, groupId, currentQuery) {

    //TODO update currentQuery object

    var query = {}
    query[groupType] = groupId


    this.dispatch(c.ROUTE.TRANSITION, { query: query })

      // this.dispatch(c.GROUP.SELECT, { type: groupType, id: groupId })
    }

  }
};

module.exports = {
  methods: methods,
  constants: c
};
