//main
var React       = require('react')

//components
var Cell 				= require('./chartSnapshotCell')




//helpers
var _ 					= require('lodash')

//logging
var log       	= require('debug')('components:indicator:table-row')

module.exports = React.createClass({

	propTypes : {
    rowData: React.PropTypes.array,
    className: React.PropTypes.string,
	},

  render: function () {
    var className = 'table-row ' + this.props.className
		return (
			<tr className={className}>
        {
          _.map(this.props.rowData, function (cellData) { //ordered
							return (
								<Cell data={cellData} />
							)
          })
        }
			</tr>

    )

  },



})
