//main
var React       = require('react')
var Fluxxor     = require('fluxxor')

//react helpers
var Panel 			= require('react-bootstrap').Panel
var FluxMixin   = Fluxxor.FluxMixin(React)

//components
var log       = require('debug')('src:components:indicator')

module.exports = React.createClass({

		propTypes : {
			flux: React.PropTypes.object
		},

	  contextTypes: {
	    router: React.PropTypes.func
	  },


  render: function () {
		log('props', this.props)
			var flux = this.props.flux
			var router = this.context.router
			log('router', router)

			var indicatorSlug = router.getCurrentParams().indicatorSlug //TODO rename as slug

			log('indicatorSlug', indicatorSlug)

			var indicator = flux.stores.indicators.query({ slug: indicatorSlug })[0]
			var data = flux.stores.data.query({ indicator: indicator.id })


			log('data', indicator, data)
    return (
			<div>the indicator</div>
    )

  },

})
