//main
var React       = require('react')

// components
var Row         = require('react-bootstrap/lib/Row')

//helpers
var _           = require('lodash')

//logging
var log         = require('debug')('components:indicator:sources')



module.exports = React.createClass({

  propTypes : {
    datasets    : React.PropTypes.array.isRequired, // datasets/timesteps
  },


  render: function () {
    log('props', this.props)

    return (
       <div className='sources'>
       <p><strong>Sources</strong></p>
        {
        _.map(this.props.datasets, function (dataset) {
          return (
          <p>
            {
              [
                <div className='dataset-title'><strong>{dataset.title + ','}</strong></div>,
                <div className='dataset-publisher'>{'published by ' + dataset.publisher + ' on'}</div>,
                <div className='dataset-date'>dataset.publicationdate</div>
              ]
            }
          </p>
          )
        })
      }
      </div>
    )


  }


})
