var React           = require('react')

//helpers
var marked      = require('utils/parse-markdown')

var log       = require('debug')('src:components:issue:indicator-preview-custom')

module.exports = React.createClass({

  propTypes: {
    date: React.PropTypes.string,
    previewfact: React.PropTypes.string
  },

  render: function () {
    log('props',this.props)

    return (
            <div className={"preview-totals"}>
              <div className={"date"}>
                <p>{this.props.date}</p>
              </div>
              <div className={"fact"} dangerouslySetInnerHTML={{
                  __html: marked(this.props.previewfact, {sanitize: true})
                  }} >
              </div>
            </div>
    )
  },


})
