var React           = require('react')

//helpers

var log       = require('debug')('src:components:issue:indicator-preview')

module.exports = React.createClass({


  render: function () {    
    var data = this.props.data
    
    var wrapStyle = { position: 'relative', paddingLeft : 0, paddingRight: 0, paddingTop : 5, paddingBottom : 5 }
    var barStyleTop = { width: data.top.length + '%', backgroundColor: data.top.colour }
    var barStyleBottom = { width: data.bottom.length + '%', backgroundColor: data.bottom.colour }
		var labelStyleTop = { position: 'relative', left: '100%', marginLeft: 4, color:data.top.colour }		
		var labelStyleBottom = { position: 'relative', left: '100%', marginLeft: 4, color:data.bottom.colour }		
    
    return (
      <div>
        <div className={"top-group"} style={{color:data.top.colour}}>
          <div>
            <strong>Least vulnerable group, {this.props.date}</strong>
          </div>
          <div>
            {data.top.label}
          </div>
          <div className='horizontal-bar-wrap'style={wrapStyle}>
            <div className='horizontal-bar'style={barStyleTop}>
              <span className='horizontal-bar-label' style={labelStyleTop}>{data.top.valueLabel}</span>
            </div>
          </div>
        </div>
        <div className={"bottom-group"} style={{color:data.bottom.colour}}>
          <div className='horizontal-bar-wrap'style={wrapStyle}>
            <div className='horizontal-bar'style={barStyleBottom}>
              <span className='horizontal-bar-label' style={labelStyleBottom}>{data.bottom.valueLabel}</span>
            </div>
          </div>
          <div>
            <strong>Most vulnerable group, {this.props.date}</strong>
          </div>
          <div>
            {data.bottom.label}
          </div>


        </div>
      
      </div>
    ) 
  },
 
 
})
