var React           = require('react')

//helpers
var Col 				= require('react-bootstrap/lib/Col')
//var Button 				= require('react-bootstrap/lib/Button')

var log       = require('debug')('src:components:issue:indicator-preview')

module.exports = React.createClass({


  render: function () {    
    
    var title = this.props.title
    var slug = this.props.slug
    var type  = this.props.type    
    
    return (
        <Col xs={12} md={4}>
          <a href="#" className="indicator-preview-tile" 
             onClick={this.handleIndicatorClick(slug)}>
            <h4>{title}</h4>
            { (type === 'range') ? this.renderRange() : this.renderCategories() }
          </a>
        </Col>
    )
  },
  
  renderRange : function() {
    var data  = this.props.data
    var groups = this.props.groups    
    
    return (
      <div>
      <div>
        <span>Most vulnerable: </span>
        <span>{data.bottom.group}</span>
        <span>{data.bottom.value}</span>
      </div>
      <div>
        <span>Least vulnerable: </span>
        <span>{data.top.group}</span>
        <span>{data.top.value}</span>
      </div>
      </div>
    ) 
  },
  renderCategories : function() {
//    var data  = this.props.data
//    var groups = this.props.groups    
    return (
      <div>Type Categories</div>
    )
  },

  handleIndicatorClick: function (indicatorSlug) {
    var flux = this.props.flux
    return function (e) {     
      flux.actions.indicator.select(indicatorSlug)
    }
  }
})
