var React           = require('react')
var _               = require('lodash')


var IndicatorPreview 				= require('./indicator-preview')

var Grid 				= require('react-bootstrap/lib/Grid')
var Row 				= require('react-bootstrap/lib/Row')

var log       = require('debug')('src:components:issue:indicators')


module.exports = React.createClass({

  render: function () {
    
    var data = this.props.flux.stores.data //.query({ indicator: indicator.id })}
    
    return (
      <Grid>      
        <Row>      
          {
              _.map(this.props.indicators, function (indicator) {   
                log('indicator ',indicator)
                return (
                    <IndicatorPreview
                      flux = {this.props.flux}
                      title = {indicator.title}
                      slug = {indicator.slug}
                      type = { indicator.datatype === 'categorical' ? 'categorical' : 'range'}
                       
                    />
                )              
              },this)
          }        
        </Row>
      </Grid>
    )
  },

})
