var React           = require('react')

var Grid 				= require('react-bootstrap/lib/Grid')
var Row 				= require('react-bootstrap/lib/Row')
var Col 				= require('react-bootstrap/lib/Col')

//helpers
var marked      = require('marked')


module.exports = React.createClass({


  render: function () {
    return (
      <Grid>
        <Row className='issue-header'>
          <Col md={12}>
            <h5 className='text-uppercase'> Issue </h5>
            <h1 className='issue-title'> {this.props.title} </h1>
          </Col>
        </Row>
        <Row className='issue-main'>
          <Col md={8}>
            <h4 className='text-uppercase'> Analysis </h4>
            <div dangerouslySetInnerHTML={{
                __html: marked(this.props.commentary, {sanitize: true})
              }} ></div>
          </Col>
          <Col md={4}>
            <h4 className={"text-uppercase"}> About this issue </h4>
            <div dangerouslySetInnerHTML={{
              __html: marked(this.props.about, {sanitize: true})
            }} ></div>
          </Col>
        </Row>
      </Grid>
    )
  },

})
