var React           = require('react')

//components
var Grid 				= require('react-bootstrap/lib/Grid')
var Row 				= require('react-bootstrap/lib/Row')
var Col 				= require('react-bootstrap/lib/Col')

//helpers
var _               = require('lodash')
var marked          = require('marked')

module.exports = React.createClass({


  render: function () {
    return (
      <Grid>
        <h2 className='recommendations-header text-uppercase'> recommendations </h2>
        <ul className='recommendation-list list'>
          {
            _.map(this.props.recommendations, function (rec, i, recommendations) {
              var className = i === recommendations.length - 1 ? 'recommendation last' : 'recommendation'
              return (
                <li className={className}>
                  <Grid>
                    <Row>
                      <Col md={1}>
                        <span>{i+1 +'.'}</span>
                      </Col>
                      <Col md={11}>
                        <div dangerouslySetInnerHTML={{
                          __html: marked(rec.title, {sanitize: true})
                        }} ></div>
                      </Col>
                    </Row>
                  </Grid>
                </li>
              )
            },this)
          }
        </ul>
      </Grid>
    )
  },

})
