var React           = require('react')
var _               = require('lodash')

var Grid 				= require('react-bootstrap/lib/Grid')

module.exports = React.createClass({


  render: function () {
    return (
      <Grid>      
        <h3> Recommendations </h3>
        <ul>
        {
          _.map(this.props.recommendations, function (rec) {   
            return (
              <li>
                {rec.title}
              </li>
            )
          },this)
        }
        
        </ul>
      </Grid>
    )
  },

})
