var React           = require('react')

var Grid            = require('react-bootstrap/lib/Grid')

//helpers
var _               = require('lodash')
var marked          = require('utils/parse-markdown')

module.exports = React.createClass({

  render: function () {
    return (
       <div>
        <h2 className='recommendations-header'> Recommendations </h2>
        <ul className='hrc-list'>
          {
            _.map(this.props.recommendations, function (rec, i) {
              var className =  'hrc-list-item'
              return (
                <li key={'recommendation-'+i} className={className}>
                  <div className="hrc-list-item-cell hrc-list-item-cell-number">
                    {i+1 +'.'}
                  </div>
                  <div
                    className="hrc-list-item-cell hrc-list-item-cell-title  marked-inner-html"
                    dangerouslySetInnerHTML={{
                      __html: marked(rec.title, {sanitize: true})
                    }} >
                  </div>
                </li>
              )
            },this)
          }
        </ul>
        </div>
    )
  },

})
