var React           = require('react')
var _               = require('lodash')

var Grid 				= require('react-bootstrap/lib/Grid')

module.exports = React.createClass({


  render: function () {
    return (
      <Grid>
        <h2 className='recommendations-header'> RECOMMENDATIONS </h2>
        <ul className='recommendation-list list'>
          {
            _.map(this.props.recommendations, function (rec, i, recommendations) {
              var className = i === recommendations.length - 1 ? 'recommendation media last' : 'recommendation media'
              return (
                <li className={className}>
                  <div className='media-left media-top'>
                    <span className='media-object'>{i+1 +'.'}</span>
                  </div>
                  <div className='media-body'>
                    <p>
                      {rec.title}
                    </p>
                  </div>
                </li>
              )
            },this)
          }
        </ul>
      </Grid>
    )
  },

})
