var React       = require('react')
var Fluxxor     = require('fluxxor')
var FluxMixin   = Fluxxor.FluxMixin(React)
var SelectGroup = require('components/select-group')

var log       = require('debug')('src:components:filter-group:index')

module.exports = React.createClass({

  propTypes: {
    // flux: React.PropTypes.func.isRequired,
    groupTypes: React.PropTypes.array.isRequired
  },

  mixins: [
    FluxMixin
  ],

  render: function () {
    var groupTypes  = this.props.groupTypes
    var flux        = this.getFlux()

    return (
      <div id='filter-group'>
        {groupTypes.map(function (groupType) {
          return <SelectGroup groupType={groupType} flux={flux} />
        })}
      </div>
    )
  }
})