var Tabletop = require('tabletop').Tabletop

module.exports = function (key, sheet, callback) {
  return function () {
    var tabletop = Tabletop.init({
      key: key,
      wanted: [sheet],
      callback: callback,
      parseNumbers: true,
      // proxy: "http://tewdata.s3.amazonaws.com/",
    })
  }
}
