var Tabletop = require('tabletop').Tabletop

module.exports = function (key, sheet, callback) {
  return function () {
    var tabletop = Tabletop({
      key: key,
      wanted: [sheet],
      callback: callback,
      parseNumbers: true,
      // simple_url: true,
      // singleton: true,
      // endpoint: "http://tewdata.s3.amazonaws.com",
      // proxy: 'http://tewdata.s3.amazonaws.com',
      debug: true
    })
  }
}
