var React         = require("react"),
    Router        = require("react-router"),
    Route         = Router.Route,
    DefaultRoute  = Router.DefaultRoute;

var Main          = require('components/main');
var Intro         = require('components/intro')
var Issue         = require('components/issue')
var Indicator     = require('components/indicator')

//TODO make these work
var routes = (
  <Route path="/" handler={Main} name="home" >
    <DefaultRoute handler={Intro} name="intro" />
    <Route path="/employment" handler={Issue} name="employment" />
    <Route path="/discrimination" handler={Issue} name="discrimination" />
    <Route path="/pay" handler={Issue} name="pay" />
    <Route path="/leadership" handler={Issue} name="leadership" />
    <Route path="/*/indicators/:indicatorName" handler={Indicator} name="indicator" />
  </Route>
);

module.exports = routes;
