var React         = require("react")
var Router        = require("react-router")
var About         = require('components/about')
var Main          = require('components/main')
var Intro         = require('components/intro')
var Issue         = require('components/issue')
var Indicator     = require('components/indicator')

var Route         = Router.Route
var DefaultRoute  = Router.DefaultRoute

require('string.prototype.startswith')

var routes = (
  <Route name="home" path={"/"} handler={Main}  >
    <DefaultRoute name="intro" handler={Intro} />
    <Route name="about"     path={"/about"}                     handler={About} />
    <Route name="issue"     path={"/issue/:issueSlug"}          handler={Issue} ignoreScrollBehavior />
    <Route name="indicator" path={"/indicator/:indicatorSlug"}  handler={Indicator} ignoreScrollBehavior />
  </Route>
);

module.exports = routes;
