module.exports = function (sets) {      
    var end  = sets.length - 1
    var result = []

    function addTo(curr, start) {
      var first = sets[start]
        , last  = (start === end)

      for (var i = 0; i < first.length; ++i) {
        var copy = curr.slice()
        copy.push(first[i])

        if (last) {
          result.push(copy)
        } else {
          addTo(copy, start + 1)
        }
      }
    }

    if (sets.length) {
      addTo([], 0)
    } else {
      result.push([])
    }
    return result

} 
     
