module.exports = function (value, refValue,sortDesc) {       
    if (typeof value === 'boolean') {
      //value interpreted as top or bottom
      return value ? '#0095AC' : '#E19900'      
    } else {
      if (typeof refValue === 'undefined'  || typeof value === 'undefined' ) {
        return '#E19900'
      } else {
        return (sortDesc && value <= refValue || !sortDesc && value > refValue ) ? '#0095AC' : '#E19900'
      }
    }
}
