module.exports = function (sortDesc, value, refDatum) {      
    refDatum = typeof refDatum !== 'undefined' ? refDatum : ''
    if (typeof refDatum !== 'undefined') {
      return 'blue'
    } else {
      return (this.props.sortDesc && value < refDatum.value || !this.props.sortDesc && value > refDatum.value ) ? 'blue' : 'orange'
    }
      
}
