module.exports = function (sortDesc, value, refValue) {       
    if (typeof value !== 'undefined') {
      if (typeof refValue === 'undefined' ) {
        return '#EDB61A'
      } else {
        return (sortDesc && value <= refValue || !sortDesc && value > refValue ) ? '#6FBDD9' : '#EDB61A'
      }
    } else {
      var top = sortDesc
      return top ? '#6FBDD9' : '#EDB61A'
    }
}
