module.exports = function (sortDesc, value, refDatum) {          
    if (typeof refDatum === 'undefined' || typeof refDatum.value === 'undefined' ) {
      return 'gray'
    } else {
      return (sortDesc && value <= refDatum.value || !sortDesc && value > refDatum.value ) ? 'blue' : 'orange'
    }
      
}
