module.exports = function (sortDesc, value, refValue) {       
    if (typeof value !== 'undefined') {
      if (typeof refValue === 'undefined' ) {
        return '#E19900'
      } else {
        return (sortDesc && value <= refValue || !sortDesc && value > refValue ) ? '#0095AC' : '#E19900'
      }
    } else {
      var top = sortDesc
      return top ? '#0095AC' : '#E19900'
    }
}
