var _ = require('lodash')
module.exports = function (data,indicator) {      
  var range = {}
  var breakdowns = indicator.breakdowns.split(',')       
  data = 
    _(data)
      .reject(function(d){
        var all = true
        _.each(breakdowns, function(bd){
          if (d[bd] !== 'all') {
            all = false
          }
        })
        return all
      })
      .sortByOrder(['value'],[indicator.order === 'DESC']).value()

return { top: _.first(data), bottom : _.last(data)}

} 
     
