module.exports = function (value,type,unit) {      
    unit = typeof unit !== 'undefined' ? unit : ''
    type = typeof type !== 'undefined' ? type : ''
    
    var label
    
    if (type === 'percentage' || type === 'part-to-whole') {
      // round to first decimal
      label = (Math.round(value * 1000)/10).toString() + unit
    } else if (type === 'median') {
      label = unit + ' ' + value.toString()
    } else {
      label = value.toString() + unit
    }   
    
    return label;
      
}
